mapper.select:
SELECT STUDY_TITLE, STUDY_CONTENTS, STUDY_LOCATION, STUDY_TOTAL, "OPEN", STUDY_BLOCKED, STUDY_DEADLINE_DATE, STUDY_REGIST_DATE, STUDY_UPDATE_DATE, MEMBER_ID FROM TBL_STUDY WHERE MEMBER_ID = #{memberId}
INSERT INTO TBL_STUDY (ID, STUDY_TITLE, STUDY_CONTENTS, STUDY_TOTAL, STUDY_DEADLINE_DATE, MEMBER_ID) VALUES (SEQ_STUDY.NEXTVAL, #{studyTitle}, #{studyContents}, #{studyTotal}, SYSDATE, #{memberId})
SELECT S.ID, STUDY_TITLE, M.MEMBER_NAME, STUDY_REGIST_DATE FROM TBL_STUDY S JOIN TBL_MEMBER M ON S.MEMBER_ID = M.ID;
